// TestEqualFromInput tests Equal using some big inputs
func TestEqualFromInput(t *testing.T) {
	inputPath := "input.txt"
	f, err := os.Open(inputPath)
	if err != nil {
		t.Fatal(err)
	}
	defer f.Close()
	outputPath := "output.txt"
	output, err := ioutil.ReadFile(outputPath)
	if err != nil {
		t.Fatal(err)
	}
	var outputSteps []int
	for _, el := range bytes.Fields(output) {
		el, err := strconv.Atoi(string(el))
		if err != nil {
			t.Fatal(err)
		}
		outputSteps = append(outputSteps, el)
	}
	scanner := bufio.NewScanner(f)
	buf := make([]byte, 64*1024*1024)
	scanner.Buffer(buf, 64*1024*1024)
	tests := -1
	var failedTests []int
	for scanner.Scan() {
		tests++
		var arr []int
		lastIndex := 0
		data := scanner.Bytes()
		for i, c := range data {
			var index int
			if c == ' ' {
				index = i
			} else if i == len(data)-1 {
				index = i + 1
			} else {
				continue
			}
			el, err := strconv.Atoi(string(data[lastIndex:index]))
			if err != nil {
				t.Fatal(err)
			}
			lastIndex = i + 1
			arr = append(arr, el)
		}
		if len(arr) != 10000 {
			t.Fatal("wrong array length", len(arr))
		}
		if r := Equal(arr); r != outputSteps[tests] {
			failedTests = append(failedTests, tests)
			t.Errorf("calculated: %d, expected: %d, diff: %d", r, outputSteps[tests], r-outputSteps[tests])
		}
	}
	t.Log("failed tests", failedTests)
}
